INSERT INTO `jobs` (`id`, `category_id`, `job_level_id`, `lang_id`, `trade_id`, `job_code`, `cover`, `name`, `description`, `requirement`, `benefit`, `num_of_candidate`, `short_requirement`, `location`, `address`, `working_hours`, `start_time`, `end_time`, `overtime`, `note`, `salary_from`, `salary_to`, `unit`, `contact_name`, `contact_phone`, `contact_email`, `status`, `hot`, `urgently`, `company_name`, `company_number_em`, `company_profile`, `view`, `alias`, `friendly_title`, `picture`, `meta_desc`, `meta_key`, `publish_start`, `publish_end`, `created`, `updated`, `hot_date`, `created_by`, `is_change`, `ipartner`, `source_id`, `company_id`, `friendly_url`, `user_id`) VALUES
	(1, '2', 4, NULL, NULL, 'J3762', NULL, 'THÔNG DỊCH BIÊN DỊCH TIẾNG NHẬT', '- Dịch trong các buổi họp của công ty, và các tài liệu liên quan\r\n- Truyền đạt lại chỉ thị của người Nhật cho nhà máy\r\n- Hỗ trợ các công việc khác theo yêu cầu cấp trên\r\n', '- Tiếng Nhật N1, không yêu cầu bằng\r\n- Có kinh nghiệm phiên dịch, biên dịch từ 1 năm trở lên\r\n【Ưu tiên】\r\n  ・Ứng viên có kinh nghiệm làm việc ở Nhà máy của Nhật\r\n  ・Ứng viên du học\r\n', '- Tăng lương: 1 lần / năm\r\n- Thưởng:1 lần/ năm, theo thành tích của công ty\r\n- Xe đưa đón: từ HCM đến công ty\r\n- Ngày nghỉ Lễ và chế độ bảo hiểm, trợ cấp tăng ca theo Luật Việt nam', 1, NULL, '5', 'KCN VSIP 1 ', NULL, '2', '6', '1', '- Thứ 7 làm việc cách tuần\r\n- Tăng ca có thể phát sinh', 800, 1200, '$', NULL, NULL, NULL, 1, NULL, NULL, '', '30', 'Công ty 100% vốn đầu tư từ Nhật bản, có trụ sở chính tại Nhật, nhà máy ở Việt Nam được thành lập năm 2015 tại VSIP 1, Bình Dương.  Lĩnh vực sản xuất chủ yếu là các loại dụng cụ, trang thiết bị dùng trong y tế.\r\n', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2017-12-15 00:00:00', '2017-12-18 10:12:23', NULL, NULL, 0, 1, 1, 0, 'thong-dich-bien-dich-tieng-nhat-1', 0),
	(2, '24', 3, 3, NULL, 'J3719', NULL, 'QUẢN LÝ BỘ PHẬN ĐÚC KHUÔN & NHỰA', '- Gia công, sản xuất, đúc các sản phẩm nhựa\r\n- Vận hành máy móc\r\n- Quản lý trình tự làm việc của công nhân với vai trò là trưởng bộ phận lắp đặt\r\n- Đúc khuôn những sản phẩm như ruy băng in máy in, linh kiện điện tử khác, ...\r\n- Kiểm tra, gia công, đóng gói thành phẩm\r\n- Quản lý đơn đặt hàng, quản lý ngày giao hàng, kiểm soát chất lượng, mua sắm vật tư, kinh doanh vận tải\r\n\r\n【Ưu tiên】\r\n  ・Ứng viên sống ở khu vực Long Bình', '- Có kinh nghiệm đúc khuôn\r\n- Am hiểu về khuôn\r\n- Tốt nghiệp cao đẳng\r\n- Tiếng Nhật N3 trở lên', '- Sẽ được trang bị những kỹ năng cần thiết để tiến hành công việc\r\n- Có xe đưa rước từ TP.HCM\r\n- Tăng lương 1 năm 1 lần\r\n- Thưởng : 1 năm 1 lần ( 1 tháng lương )', 1, NULL, '3', 'Long Bình Techno Park, Biên Hòa, Đồng Nai', NULL, '2', '6', NULL, '-	Làm cách tuần ngày thứ 7\r\n-	Có tăng ca theo yêu cầu\r\n', 1000, 14000, '$', NULL, NULL, NULL, 1, NULL, NULL, '', '291', 'Công ty Nhật chuyên sản xuất ruy băng máy in, linh kiện điện tử\r\n', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2017-12-20 00:00:00', '2018-01-10 11:47:30', NULL, NULL, 0, 1, 1, 0, 'quan-ly-bo-phan-duc-khuon-nhua-j3719-2', 0),
	(3, '3', 5, 2, NULL, 'J3737', NULL, 'TRỢ LÝ TRƯỞNG PHÒNG KINH DOANH', '- Thông dịch trong cuộc họp, viết mail, tài liệu,… những công việc liên quan đến tiếng Nhật.\r\n- Viết báo cáo, kế hoạch kinh doanh cho quản lý doanh nghiệp, báo cáo về các thành viên khác,...\r\n', '- Tốt nghiệp Đại học\r\n- Năng lực nhật ngữ N2 trở lên\r\n- Có khả năng giao tiếp tốt\r\n- Không cần kinh nghiệm\r\n- Trung thực, tích cực trong công việc', 'Được hưởng đầy đủ các chế độ theo luật lao động Việt Nam ( BHXH, BHYT, BHTN )\r\n・ Lương + lương thưởng cuối năm hấp dẫn theo chính sách công ty\r\n・Tăng lương thường niên, Tăng cấp theo năng lực,\r\n・Trợ cấp đi lại, Đồng phục, cơm trưa...\r\n・Được nghỉ 2 ngày thứ 7 và 4 ngày chủ nhật trong tháng.\r\n・Môi trường công ty Nhật có tính kỷ luật cao.\r\n・Được khám sức khỏe định kỳ.\r\n・Du lịch, Teambuilding và các sự kiện văn hóa khác.', 1, NULL, '3', 'Khu công nghiệp Long Đức, Long Thành, Đồng Nai', NULL, '2', '6', NULL, '- 	Làm cách tuần ngày thứ 7\r\n-	Có tăng ca theo yêu cầu\r\n', 400, 500, '$', NULL, NULL, NULL, 1, NULL, NULL, '', '2000', 'Công ty cung cấp và sản xuất các sản phẩm cửa, cửa sổ, thiết bị ngoại thất... bằng nhôm, nhựa cao cấp chất lượng Nhật trên toàn nước', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2017-12-20 00:00:00', '2018-01-10 11:48:15', NULL, NULL, 0, 1, 1, 0, 'tro-ly-truong-phong-kinh-doanh-j3737-3', 0),
	(4, '11', 4, 2, NULL, 'J3738', NULL, 'GIÁM SÁT KINH DOANH', '- Quản lý các thành viên trong team kinh doanh\r\n- Chăm sóc khách hàng và tìm kiếm khách hàng mới\r\n- Xây dựng mối quan hệ tốt với khách hàng', '- Năng lực nhật ngữ N2 trở lên\r\n- Có hơn 2 năm kinh nghiệm trong kinh doanh\r\n- Tốt nghiệp Đại học\r\n- Tiếng Anh trung cấp', ' Được hưởng đầy đủ các chế độ theo luật lao động Việt Nam ( BHXH, BHYT, BHTN )\r\n・ Lương + lương thưởng cuối năm hấp dẫn theo chính sách công ty\r\n・Tăng lương thường niên, Tăng cấp theo năng lực,\r\n・Trợ cấp đi lại, Đồng phục, cơm trưa...\r\n・Được nghỉ 2 ngày thứ 7 và 4 ngày chủ nhật trong tháng.\r\n・Môi trường công ty Nhật có tính kỷ luật cao.\r\n・Được khám sức khỏe định kỳ.\r\n・Du lịch, Teambuilding và các sự kiện văn hóa khác.\r\nThông tin nhà tuyển dụng', 1, NULL, '3', 'Khu công nghiệp Long Đức, Long Thành, Đồng Nai', NULL, '2', '6', NULL, '- Nghỉ thứ 7 cách tuần\r\n- Tăng ca theo yêu cầu', 600, 700, '$', NULL, NULL, NULL, 1, NULL, NULL, '', '2000', 'Công ty cung cấp và sản xuất các sản phẩm cửa, cửa sổ, thiết bị ngoại thất... bằng nhôm, nhựa cao cấp chất lượng Nhật trên toàn nước', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2017-12-20 00:00:00', '2018-01-10 11:48:43', NULL, NULL, 0, 1, 1, 0, 'giam-sat-kinh-doanh-j3738-4', 0),
	(5, '18', 4, 2, NULL, 'J3739', NULL, 'NHÂN VIÊN KINH DOANH', '- Viết báo cáo công việc cho quản lý người Nhật\r\n- Liên lạc với khách hàng, soạn hợp đồng, làm báo giá gửi khách hàng\r\n- Quản lí nội dung liên quan đến các sản phẩm mới, các đơn hàng, làm bản vẽ', '- Năng lực nhật ngữ N2 trở lên\r\n- Giới tính: NỮ\r\n- Có hơn 1 năm kinh nghiệm trong kinh doanh\r\n- Tốt nghiệp Cao đẳng, Đại học chuyên ngành kinh doanh\r\n- Tiếng Anh trung cấp', 'Được hưởng đầy đủ các chế độ theo luật lao động Việt Nam ( BHXH, BHYT, BHTN )\r\n・ Lương + lương thưởng cuối năm hấp dẫn theo chính sách công ty\r\n・Tăng lương thường niên, Tăng cấp theo năng lực,\r\n・Trợ cấp đi lại, Đồng phục, cơm trưa...\r\n・Được nghỉ 2 ngày thứ 7 và 4 ngày chủ nhật trong tháng.\r\n・Môi trường công ty Nhật có tính kỷ luật cao.\r\n・Được khám sức khỏe định kỳ.\r\n・Du lịch, Teambuilding và các sự kiện văn hóa khác.', 1, NULL, '3', 'Khu công nghiệp Long Đức, Long Thành, Đồng Nai', NULL, '2', '2', NULL, '- Nghỉ thứ 7 cách tuần\r\n- Có tăng ca theo yêu cầu', 400, 500, '$', NULL, NULL, NULL, 1, NULL, NULL, '', '2000', 'Công ty cung cấp và sản xuất các sản phẩm cửa, cửa sổ, thiết bị ngoại thất... bằng nhôm, nhựa cao cấp chất lượng Nhật trên toàn nước', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2017-12-20 00:00:00', '2018-01-10 11:49:20', NULL, NULL, 0, 1, 1, 0, 'nhan-vien-kinh-doanh-5', 0),
	(6, '2', 5, 2, NULL, 'J3740', NULL, 'BIÊN PHIÊN DỊCH TIẾNG NHẬT ', '- Phiên dịch trong mail, tài liệu,… những công việc liên quan đến tiếng Nhật.\r\n- Phiên dịch, thông dịch trong cuộc họp\r\n', '- Năng lực nhật ngữ N2 trở lên\r\n- Giới tính : nữ\r\n- Tốt nghiệp đại học chuyên ngành\r\n- Không cần kinh nghiệm\r\n\r\n【Ưu tiên】\r\n  ・Có kinh nghiệm trong ngành chế tạo\r\n', 'Được hưởng đầy đủ các chế độ theo luật lao động Việt Nam ( BHXH, BHYT, BHTN )\r\n・ Lương + lương thưởng cuối năm hấp dẫn theo chính sách công ty\r\n・Tăng lương thường niên, Tăng cấp theo năng lực,\r\n・Trợ cấp đi lại, Đồng phục, cơm trưa...\r\n・Được nghỉ 2 ngày thứ 7 và 4 ngày chủ nhật trong tháng.\r\n・Môi trường công ty Nhật có tính kỷ luật cao.\r\n・Được khám sức khỏe định kỳ.\r\n・Du lịch, Teambuilding và các sự kiện văn hóa khác', 1, NULL, '3', 'Khu công nghiệp Long Đức, Long Thành, Đồng Nai', NULL, '2', '6', NULL, '- Nghỉ thứ 7 cách tuần\r\n- Có tăng ca theo yêu cầu\r\n', 400, 500, '$', NULL, NULL, NULL, 1, NULL, NULL, '', '2000', 'Công ty cung cấp và sản xuất các sản phẩm cửa, cửa sổ, thiết bị ngoại thất... bằng nhôm, nhựa cao cấp chất lượng Nhật trên toàn nước', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2017-12-20 00:00:00', '2018-01-10 10:01:44', NULL, NULL, 0, 1, 1, 0, 'bien-phien-dich-tieng-nhat-6', 0),
	(7, '24', 4, 3, NULL, 'J3741', NULL, 'NHÂN VIÊN THI CÔNG', 'Dự án của công ty đang thực hiện là dự án cải tạo môi trường nước khu vực TPHCM giai đoạn 2. Áp dụng phương pháp cải tiến của Nhật bản để làm, do đó chủ yếu công việc sẽ được thực hiện dựa trên sự chỉ đạo của sếp người Nhật.\r\n\r\nTeam hiện tại : khoảng 15 nhân viên, sếp trực tiếp là người Nhật.\r\nNhu cầu nhân lực trong từng dự án đang thiếu, nên công ty cần tuyển thêm khoảng 8 bạn cho vị trí này.', '- Trình độ tiếng Nhật N3 trở lên\r\n- Giới tính : Nam\r\n- Có kinh nghiệm trong mảng thi công xây dựng hoặc liên quan đến vận hành máy móc. Ưu tiên các bạn Tu nghiệp sinh\r\n- Chịu khó, không ngại tăng ca\r\n\r\n【Ưu tiên】\r\n  ・Có kinh nghiệm trong lĩnh vực hàn', '- Xét tăng lương mỗi năm một lần\r\n- Lương thưởng mỗi năm\r\n- Được nghỉ Chủ Nhật mỗi tuần.\r\n- Được hưởng trợ cấp tăng ca, trợ cấp nếu làm ban đêm phát sinh', 8, NULL, '1', 'Quận 1, TP.HCM', NULL, '2', '7', NULL, '- Tăng ca theo yêu cầu', 400, 500, '$', NULL, NULL, NULL, 1, NULL, NULL, '', '55', 'Chuyên thi công công trình khoan kích ống ngầm trong lĩnh vực cấp thoát nước đô thị tại Nhật Bản.\r\nThành lập ở Nhật năm 1975, là công ty xây dựng chuyên thi công công trình khoan kích ống ngầm trong lĩnh vực cấp thoát nước đô thị tại Nhật Bản.', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2017-12-21 00:00:00', '2018-01-10 11:50:41', NULL, NULL, 0, 1, 1, 0, 'nhan-vien-thi-cong-7', 0),
	(8, '25', 5, 3, NULL, 'J3217', NULL, 'NHÂN VIÊN ĐIỀU PHỐI', '-	Nhận đơn đặt hàng từ phía Nhật Bản\r\n-	Quản lí đơn hàng của khách hàng cho các mạng lưới dịch vụ toàn cầu chẳng hạn như Internert VON, IPVPN\r\n-	Phối hợp với các đối tác toàn cầu để xử lí đơn hàng\r\n-	Hỗ trợ các phòng ban khác trong hoạt động bảo trì, cải tiến', '-	Tiếng Nhật N3\r\n-	Tiếng Anh thông thạo\r\n-	Có kỹ năng giao tiếp\r\n-	Tư duy tích cực, mong muốn học hỏi', '-	Được training công việc\r\n-	Cải thiện bản thân với các lớp học tiếng Anh với người bản ngữ\r\n-	Bảo hiểm tai nạn 24/24\r\n-	Tiệc công ty, du lịch', 5, NULL, '1', 'Quận 12, TP.HCM', NULL, '2', '6', NULL, '- Tăng ca theo yêu cầu', 350, 400, '$', NULL, NULL, NULL, 1, 0, NULL, '', '90', 'cung cấp dịch vụ mạng viễn thông\r\n100% vốn đầu tư từ Nhật', 0, NULL, NULL, NULL, '', '', NULL, NULL, '2017-12-21 00:00:00', '2018-10-24 00:10:01', NULL, NULL, 0, 1, 1, 0, 'nhan-vien-dieu-phoi-8', 0),
	(9, '22', 4, 3, NULL, 'J3733', NULL, 'NHÂN VIÊN NAM QA', '- kiểm tra sản phẩm có đáp ứng được chất lượng được yêu cầu hay không.\r\n- Bao gồm tất cả các hoạt động như thiết kế, phát triển, sản xuất, dịch vụ và tài liệu.\r\n- Tóm tắt kết quả kiểm tra.\r\n- Dịch tài liệu liệu từ tiếng Nhật sang tiếng Việt.\r\n- Đối ứng khiếu nại ( đối ứng mail bằng tiếng Nhật, tiếng Anh)', '- Tiếng Nhật N2 hoặc N3\r\n- Tiếng Anh giao tiếp sơ trung\r\n- Trên 4 năm kinh nghiệm QA\r\n- Có hiểu biết và kinh nghiệm về ISO9001', '- Có xe buýt đưa đón từ nội thành  TP.HCM  ( hoặc trợ cấp tiền xăng xe 500000VND/tháng)\r\n- Căng tin miễn phí\r\n- Thưởng 1 năm 1 lần ( 1 tháng lương)\r\n- Tăng lương 1 năm 1 lần', 1, NULL, '5', 'Khu Công nghiệp VSIP II, Thủ Dầu Một, Bình Dương', NULL, '2', '6', NULL, '- Phỏng vấn qua điện thoại -> Phỏng vấn với quản lý người Việt\r\n- Nghỉ thứ 7 cách tuần\r\n- Có đi công tác 1 năm 2-3 lần ở Nhật, Philippin\r\n', 18000000, 20000000, 'VND', NULL, NULL, NULL, 1, NULL, NULL, '', '500', 'công ty Nhật Bản, chuyên về sản xuất các loại phích cắm như phích cắm điện, phích cắm trục, phích cắm cáp quang…', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2017-12-21 00:00:00', '2018-01-10 11:52:51', NULL, NULL, 0, 1, 1, 0, 'nhan-vien-nam-qa-9', 0),
	(10, '17', 3, NULL, NULL, 'J3735', NULL, 'TRƯỞNG BỘ PHẬN LOGISTICS', '• Quản lý kho tổng thể bao gồm:\r\n* Quản lý hàng hoá nhập khẩu\r\n- Đảm bảo hướng dẫn về kế hoạch xuất khẩu hàng hoá và lập kế hoạch đóng gói hàng đúng quy định.\r\n- Tiến hành và quản lý hệ thống nội bộ liên quan đến quản lý WH để đảm bảo tiến độ đóng gói hàng hoá. Báo cáo kết quả này cho trụ sở chính tại Nhật Bản.\r\n- Quản lý và quản lý hàng hoá xuất nhập khẩu, hàng tồn kho, khu vực cho thuê WH\r\n* Quản lý hoạt động của WH\r\n- Quản lý nhân sự WH\r\n- Đảm bảo quy trình tuần tra 5s / 5s\r\n- Kiểm soát / kiểm tra hóa đơn liên quan đến hoạt động của WH \r\n- Phối hợp làm báo giá (Chi phí / bán hàng)\r\n- Báo cáo hàng tháng cho Trưởng phòng Logistics\r\n- Tổ chức đào tạo chuyên môn cho tất cả các thành viên của WH khi được yêu cầu.\r\n- Nguồn cho các nhà cung cấp mới để tối đa hóa doanh thu của WH khi được yêu cầu.\r\n- Duy trì quan hệ tốt với các đối tác của công ty (Đại lý, Hải quan ...)\r\n- Hoàn thành các nhiệm vụ khác do Tổng giám đốc yêu cầu', '• Có kinh nghiệm trong lĩnh vực Quản lý Kho hàng, có kinh nghiệm trong Công ty Dịch vụ Logistics, đặc biệt có mối quan hệ tốt với các đại lý vận tải, giao nhận hàng hóa\r\n• Tốt nghiệp trường Đại học Tài chính và Hải quan, Đại học Ngoại thương hoặc Quản trị Kinh doanh\r\n• Yêu cầu trình độ Tiếng Anh / tiếng Nhật cao\r\n\r\n【Ưu tiên】\r\n  ・Địa chỉ cư trú: Quận 9 hoặc Thủ Đức. Nếu có thể, Biên Hòa, Đồng Nai là tốt nhất.\r\n', '- Nghi lễ theo lịch Vệt Nam', 1, NULL, '3', 'Long Bình, Biên Hòa, ĐỒNG nAI', NULL, '2', '6', NULL, '- 30 tuổi\r\n- Có đi công tác theo yêu cầu\r\n- ', 800, 900, '$', NULL, NULL, NULL, -1, NULL, NULL, '', '882', '- Công ty có trụ sở chính ở Kanagawa, Nhật Bản\r\n- Chuyên về LOGISTIC', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2017-12-21 00:00:00', '2019-01-10 10:38:41', NULL, NULL, 0, 1, 1, 0, 'truong-bo-phan-logistics-10', 0),
	(11, '2', 4, 2, NULL, 'J3343', NULL, 'THÔNG DỊCH KIÊM TỔNG VỤ', '-	Biên dịch tài liệu Việt – Nhật\r\n-	Phiên dịch trong các cuộc họp của công ty\r\n-	Phiên dịch cho khách hàng trong các cuộc giao dịch\r\n-	Thực hiện các nghiệp vụ tổng hợp khác: Quản lí lao động, thu mua....', '-	Tiếng Nhật N2 \r\n-	Có trên 1 năm kinh nghiệm làm việc sử dụng tiếng Nhật\r\n   \r\n【Ưu tiên】\r\n  ・Nam\r\n', '- Xe đưa đón trừ văn phòng trong nội thành TP đến nhà máy\r\n- Xét lương, lương thưởng mỗi năm ', 1, NULL, '1', 'Biên hòa, Đồng Nai', NULL, '2', '6', NULL, '- Làm thứ 7 cách tuần', 10000000, 12000000, 'VND', NULL, NULL, NULL, 1, NULL, NULL, '', '300', '- Công ty Nhật\r\n- Cung cấp dược phẩm, đặc biệt là các chất làm giảm đau', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2017-12-21 00:00:00', '2018-01-10 11:54:12', NULL, NULL, 0, 1, 1, 0, 'thong-dich-kiem-tong-vu-11', 0),
	(12, '22', 3, NULL, NULL, 'H2669', NULL, 'TRƯỞNG NHÓM QUẢN LÝ CHẤT LƯỢNG', '- Quản lý chất lượng các linh kiện ô tô, linh kiện lắp ráp\r\n- Quản lý chất lượng và cải tiến sản xuất\r\n- Tổng hợp thông tin và viết báo cáo\r\n- Quản lý nhân viên của bộ phận quản lý chất lượng\r\n', '- Nam, có kinh nghiệm quản lý chất lượng\r\n- Tiếng Nhật N3 trở lên hoặc tiếng Anh trung cấp\r\n', '- Xét tăng lương: 1 lần / năm\r\n- Đóng bảo hiểm Xã hội, Y tế  theo luật Việt Nam\r\n- Phúc lợi khác: có xe đưa đón từ Hà Nội\r\n', 1, NULL, '22', 'KCN Thăng Long II, Hưng Yên ', NULL, '2', '7', NULL, '- Làm cách tuần ngày thứ 7', 10000000, 15000000, 'VND', NULL, NULL, NULL, 1, NULL, NULL, '', '370', 'Công ty về lĩnh vực chuyên sản xuất thiết bị điện - điện tử của Nhật Bản', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2017-12-21 00:00:00', '2017-12-21 14:02:27', NULL, NULL, 0, 1, 1, 0, 'truong-nhom-quan-ly-chat-luong-12', 0),
	(13, '2', 4, NULL, NULL, 'J3715', NULL, 'NHÂN VIÊN BIÊN PHIÊN DỊCH TIẾNG NHẬT KIÊM TỔNG VỤ', '- Quản lý những công việc về tiêu thụ hàng hóa, trang thiết bị, tài sản, tài liệu,…\r\n- Đánh giá nhân viên \r\n- Tuyển dụng nhân sự đồng thời đào tạo nhân viên mới\r\n- Lên kế hoạch, quản lý các sự kiện trong công ty\r\nCùng một số công việc sẽ được trao đổi cụ thể khi phỏng vấn', '- Giới tính nữ\r\n- Trình độ tiếng Nhật N2\r\n- Có ít nhất 2 năm kinh nghiệm làm công ty\r\n- Tiếng Anh tốt, có khả năng giao tiếp\r\n\r\n【Ưu tiên】\r\n  ・ Những ứng viên đã có kinh nghiệm làm phiên dịch hoặc tổng vụ\r\n  ・Những ứng viên khoảng 30 tuổi\r\n\r\n', '- Có xe đưa rước từ TP.HCM\r\n- Tăng lương 1 năm 1 lần\r\n- Thưởng 1 năm 1 lần\r\n- Được nghỉ thứ 7 ,Chủ Nhật mỗi tuần \r\nCùng nhiều chế độ đãi ngộ hấp dẫn khác', 1, NULL, '5', 'Khu Công nghiệp Mỹ Phước III, Bến Cát, Bình Dương', NULL, '2', '6', NULL, '- Giới tính: Nữ', 500, 700, '$', NULL, NULL, NULL, 1, NULL, NULL, '', '1000', 'công ty 100% vốn đầu tư nước ngoài, chuyên nhập khẩu và lắp ráp máy nông nghiệp cung cấp cho thị trường nông nghiệp\r\n', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2017-12-21 00:00:00', '2017-12-21 14:23:54', NULL, NULL, 0, 1, 1, 0, 'nhan-vien-bien-phien-dich-tieng-nhat-kiem-tong-vu-13', 0),
	(14, '4', 4, 2, NULL, 'J2958', NULL, 'TỔNG VỤ NHÂN SỰ', '- Thực hiện toàn bộ các công việc liên quan đến tổng vụ nhân sự\r\n- Quản lý cơ sở dữ liệu của nhân viên\r\n- Cập nhật thông tin nhân viên\r\n- Hỗ trợ quản lí về phúc lợi y tế, các qui định tuyển dụng nhân viên….\r\n- Đào tạo nhân viên mới\r\n- Báo cáo kinh doanh hàng tháng', '- Có kinh nghiệm tổng vụ từ 2 năm trở nên\r\n- Tiếng Nhật trên N2, giao tiếp tốt.\r\n- Đã từng đi làm công ty', '- Lương tháng 13\r\n- Xét tăng lương 1 năm 1 lần\r\n- Chế độ bảo hiểm, phúc lợi theo Luật Việt Nam', 1, NULL, '1', 'Tân Bình, TP.HCM', NULL, '2', '6', NULL, '- Giới tính: Nữ\r\n- Nghỉ thứ 7 cách tuần', 900, 1000, '$', NULL, NULL, NULL, 1, NULL, NULL, '', '10', '- Là công ty Nhật hoạt động trong lĩnh vực chuyển phát nhanh.\r\n- Trực thuộc tập đoàn hàng đầu của Nhật về dịch vụ giao nhận, đang mở rộng phát triển tại thị trường Việt Nam.', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2017-12-21 00:00:00', '2018-01-10 11:54:58', NULL, NULL, 0, 1, 1, 0, 'tong-vu-nhan-su-14', 0),
	(15, '22', 4, 2, NULL, 'J3732', NULL, 'NHÂN VIÊN KIỂM ĐỊNH CHẤT LƯỢNG', '- Kiểm tra chất lượng các ống thông niệu đạo y tế. Vì vận chuyển hàng hóa đi khắp nơi trên thế giới nên cần tiếng Anh\r\n- Công việc của QA là bao hàm luôn cả những việc Thiết kế, Phát triển, Sản xuất, Dịch vụ, Tài liệu liên quan\r\n•	Tóm tắt kết quả kiểm tra\r\n•	Vận chuyển hàng đi khắp thế giới\r\n•	Đối ứng bằng tiếng Anh đối với sự kiểm toán từ những nước như Hàn, Mỹ hay Châu Âu.\r\n•	Cập nhật hệ thống quản lý chất lượng QMS', '- Tốt nghiệp cao đẳng\r\n- Tiếng Nhật N2\r\n- Tiếng Anh khá\r\n- Có kinh nghiệm QA trên 1 năm\r\n- Có kinh nghiệm văn phòng và ISO9001\r\n- Có thể sử dụng được Word, Exel,… cơ bản\r\n', '- Ăn trưa miễn phí tại căng tin của công ty\r\n- Nghỉ lễ theo lịch của Việt Nam\r\n- Thưởng: 1 năm 1 lần ( trước Tết )\r\n- Tăng lương: 1 năm 1 lần\r\n- Trợ cấp phí công tác, tăng ca, xe máy\r\n- Có khả năng sẽ được đào tạo ở Nhật trong thời gian ngắn\r\n- Có cơ hội được xét làm quản lý ( Nếu làm việc lâu dài và có nhiều kinh nghiệm)', 1, NULL, '5', 'Khu Công nghiệp VSIP, Thuận An, Bình Dương', NULL, '2', '6', NULL, '- Đi công tác trong nước hoặc Nhật bản, tăng ca theo yêu cầu\r\n- ', 15000000, 18000000, 'VND', NULL, NULL, NULL, 1, 0, NULL, '', '12', 'Công ty chuyên sản xuất các sản phẩm chẳng hạn như hóa chất, nhựa chuyên dụng, sản phẩm điện tử', 0, NULL, NULL, NULL, '', '', NULL, NULL, '2017-12-21 00:00:00', '2018-10-24 00:10:01', NULL, NULL, 0, 1, 1, 0, 'nhan-vien-kiem-dinh-chat-luong-15', 0),
	(16, '2', 4, 3, NULL, 'J3761', NULL, 'TRỢ LÝ GIÁM ĐỐC NGƯỜI NHẬT', '-	Thông dịch cho bộ phận nhân sự, bộ phận quản lý, bộ phận kế toán, bộ phận sản xuất \r\n-	Truyền tải nội dung đến bộ phận kế toán, bộ phận tổng vụ', '- Tốt nghiệp đại học\r\n- Tiếng Nhật trình độ N2 trở lên, tiếng Anh đọc, viết cơ bản\r\n\r\n【Ưu tiên】\r\n  ・Ứng viên từng sống, làm việc, học tập tại Nhật \r\n  ・Ứng viên có kinh nghiệm tổng vụ\r\n', '-	Cơ hội đi công tác nước ngoài\r\n-	Xét tăng lương 1 năm 1 lần\r\n-	Phụ cấp phí công tác, đi lại\r\n-	Các chế độ, quyền lợi về ngày nghỉ tuân theo luật Việt Nam', 1, NULL, '1', 'Quận 3, TP.HCM', NULL, '2', '6', NULL, '- Tăng ca, đi công tác theo yêu cầu\r\n', 600, 1000, '$', NULL, NULL, NULL, 1, 0, NULL, '', '300', '- Là một trong những nhà sản xuất bao bì màng ghép phức hợp hàng đầu Việt Nam.\r\n- Công nghệ kỹ thuật của Nhật Bản', 0, NULL, NULL, NULL, '', '', NULL, NULL, '2017-12-21 00:00:00', '2018-10-24 00:10:01', NULL, NULL, 0, 1, 1, 0, 'tro-ly-giam-doc-nguoi-nhat-16', 0),
	(17, '17', 4, NULL, NULL, 'J3758', NULL, 'QUẢN LÍ CỬA HÀNG', '- Quản lý toàn bộ của hàng\r\n- Báo cáo với giám đốc người Nhật và quản lý doanh thu\r\n- Quản lí thiết bị với cơ sở hạ tầng của cửa hàng\r\n- Quản lí lịch làm việc của nhân viên và phụ trách công việc tuyển dụng\r\n- Xác nhận và chỉnh sửa đơn đặt hàng\r\n- Quản lí khách hàng \r\n- Soạn thảo, đề ra các chính sách thu hút khách hàng', '-Trình độ tiếng Nhật N2 \r\n- Giới tính: nữ\r\n- Tốt nghiệp Đại học\r\n- Đã từng làm ở công ty Nhật \r\n\r\n\r\n【Ưu tiên】\r\n  ・Từng làm trong ngành dịch vụ\r\n', '- Lương + lương thưởng cuối năm hấp dẫn theo chính sách công ty\r\n- Tăng lương theo năng lực, doanh số\r\n- Hưởng hoa hồng tùy theo doanh thu công ty \r\n- Tiền thưởng lên đến 300USD (nếu đạt chỉ tiêu 80 khách/tháng)\r\n- Và các quyền lợi khác sẽ được trao đổi thêm khi phỏng vấn', 1, NULL, '1', 'Quận 1, TP.HCM', NULL, '3', '8', NULL, '- Phỏng vấn với quản lý người Nhật trong thời gian 4/1 - 8/1', 450, 600, '$', NULL, NULL, NULL, 1, NULL, NULL, '', '4', 'Salon của Nhật, chuyên về tư vấn làm đẹp, làm đẹp mi', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2017-12-21 00:00:00', '2017-12-21 15:05:44', NULL, NULL, 0, 1, 1, 0, 'quan-li-cua-hang-17', 0),
	(18, '23', 3, NULL, NULL, 'H2667', NULL, 'NHÂN VIÊN QUẢN LÝ SẢN XUẤT', '- Lên kế hoạch sản xuất, giao hàng\r\n- Theo dõi tiến độ sản xuất các linh kiện\r\n- Theo dõi và giải quyết hàng tồn kho\r\n', '- Có kinh nghiệm làm việc ở công ty Nhật 2 năm trở lên\r\n- Có thể đọc bản vẽ cơ khí\r\n- Tiếng Nhật N3 trở lên\r\n', '- Thử việc 100% lương\r\n- Công ty có kế hoạch tăng lương và thăng tiến mới.\r\n- Có xe đưa đón đi phỏng vấn\r\n', 1, NULL, '22', 'KCN Phố Nối A, Hưng Yên', NULL, '2', '6', NULL, 'Thời gian làm việc: 08:00 ~ 17:00 ', 450, 500, '$', NULL, NULL, NULL, 1, NULL, NULL, '', '1700', 'Công ty Nhật Bản chuyên sản xuất và kinh doanh máy móc, thiết bị điện tử', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2017-12-21 00:00:00', '2017-12-21 15:08:58', NULL, NULL, 0, 1, 1, 0, 'nhan-vien-qua-n-ly-sa-n-xua-t-18', 0),
	(19, '25', 4, 3, NULL, 'J3193', NULL, 'NHÂN VIÊN THU MUA', '• Mua các vật liệu cần thiết\r\n• Phát triển các nhà cung cấp mới\r\n• Đàm phán giá với các nhà cung cấp hiện có\r\n• Tạo hồ sơ mua hàng\r\n• Hợp tác với từng bộ phận', '・Tốt nghiệp Đại học chuyên ngành Marketing, Kế toán hoặc Ngoại thương, từ 23~28 tuổi\r\n・Tiếng Hoa HSK5, hoặc tiếng Nhật N3.\r\n・Có ít nhất 1 năm kinh nghiệm về điều phối mua hàng \r\n・Thành thạo vi tính văn phòng', '- Xe đưa rước từ từ ngã tư Hàng xanh\r\n- Nghỉ phép 12 ngày/năm\r\n- Hỗ trợ phí đi lại\r\n- Xét tăng lương 1 năm/ 1 lần\r\n- Lương tháng 13', 1, NULL, '1', 'Saigon Hi-Tech Park, Quận 9, TP.HCM', NULL, '2', '2', NULL, '- Nghỉ thứ 7 cách tuần\r\n- Tăng ca theo yêu cầu', 7000000, 9000000, 'VND', NULL, NULL, NULL, 1, NULL, NULL, '', '600', 'Công ty Nhật chuyên sản xuất các thiết bị dùng trong máy ảnh kỹ thuật số và điện thoại di động.', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2017-12-21 00:00:00', '2018-01-10 13:28:03', NULL, NULL, 0, 1, 1, 0, 'nhan-vien-thu-mua-19', 0),
	(20, '24', 4, 3, NULL, 'J2762', NULL, 'NHÂN VIÊN KỸ THUẬT', '- Dịch các tài liệu liên quan đến kĩ thuật lắp ráp, các văn bản xử lý kiểm tra\r\n- Biên dịch quy trình thao tác chuẩn (SOP)\r\n- Lập báo cáo kỹ thuật\r\n- Cải tiến công đoạn và duy trì chúng\r\n- Chỉ đạo các công nhân làm việc', '- Tiếng Anh: TOEIC 500\r\n- Năng lực Nhật ngữ N3 trở lên \r\n- Thông thạo về MS Office\r\n- Đã từng làm những công việc liên quan đến kĩ thuật\r\n- Giao tiếp tốt\r\n- Hiểu được bản vẽ điện và cơ khí (CAD)\r\n\r\n\r\n【Ưu tiên】\r\n  ・Ứng viên sống tại Biên Hòa\r\n  ・Ứng viên đã từng làm việc trong công ty Nhật\r\n', '- Trợ cấp ngôn ngữ hấp dẫn (Tiếng Nhật JLPT và tiếng Anh TOEIC)\r\n- Thưởng làm việc chuyên cần; thưởng thâm niên và thưởng Tết - Cơ hội thăng tiến lên cấp quản lý. Môi trường làm việc chuyên nghiệp\r\n- Du lịch, quà tặng các dịp lễ Tết và tiệc hàng năm \r\n- Có xe đưa rước nhân viên từ Tp. HCM, Biên Hòa đến công ty.\r\n- Được nghỉ chủ nhật hàng tuần và 1 ngày thứ 7 trong tháng', 2, NULL, '3', 'Khu công nghiệp Long Thành, Đồng Nai', NULL, '2', '7', NULL, '- Tăng ca theo yêu cầu', 8700000, 13000000, 'VND', NULL, NULL, NULL, 1, NULL, NULL, '', '2000', 'được thành lập năm 2008, là Công ty 100% vốn đầu tư của tập đoàn Nhật Bản, với quy mô gồm 2.000 lao động. Công ty chuyên sản xuất/lắp ráp ống kính và các linh kiện cho máy ảnh KTS và dụng cụ cho máy nội soi.', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2017-12-21 00:00:00', '2018-01-10 13:26:41', NULL, NULL, 0, 1, 1, 0, 'nhan-vien-ky-thuat-20', 0),
	(21, '22', 3, 2, NULL, 'H2670', NULL, 'PHÓ PHÒNG QUẢN LÝ CHẤT LƯỢNG', '- Quản lý trong lĩnh vực QA\r\n- Hỗ trợ giám đốc và quản lý toàn bộ nhân viên chất lượng\r\n- Phản hồi khách hàng về các vấn đề chất lượng của sản phẩm\r\n- Phản hồi các kiểm tra về chất lượng\r\n', '- Có ít nhất 5 năm kinh nghiệm làm về chất lượng\r\n- Tiếng Nhật N2, dưới 35 tuổi\r\n- Ưu tiên người có kinh nghiệm quản lý, ở vị trí giám sát, tổ trưởng, hoặc trợ lý (không khoanh vùng quản lý bao nhiêu người)\r\n', '- Miễn phí ăn trưa, có xe đưa đón từ nội thành\r\n- Trợ cấp vị trí, tiếng Nhật N2, thuê nhà, chuyên cần và đi lại\r\n- Thưởng Tết 1 tháng lương net\r\n', 1, NULL, '2', 'KCN Thăng Long I, Đông Anh, Hà Nội', NULL, '2', '6', NULL, '- Làm cách tuần ngày thứ 7', 700, 900, '$', NULL, NULL, NULL, 1, NULL, NULL, '', '200', 'Công ty Nhật Bản chuyên sản xuất, buôn bán các sản phẩm điện tử, dây dẫn', 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2017-12-21 00:00:00', '2018-01-30 09:54:29', NULL, NULL, 0, 1, 1, 0, 'pho-phong-quan-ly-chat-luong-21', 0),
